// Load High scores
void Game::_initHighScore(){
  for(byte thisScore = 0; thisScore < this -> _NUM_HIGHSCORE; thisScore++){
    this -> _highscore[thisScore] = gb.save.get(2*thisScore);
    gb.save.get(2*thisScore + 1, this -> _name[thisScore],this ->  _NAME_LENGTH+1);
  }
}


// Save HighScore
void Game::_saveHighScore(unsigned int test_score) {

    if (test_score > (this -> _highscore[_NUM_HIGHSCORE-1])){  //if it's a highscore
          gb.getDefaultName(this -> _name[this -> _NUM_HIGHSCORE-1]);
          gb.gui.keyboard("Name", this -> _name[_NUM_HIGHSCORE-1]);
          this -> _highscore[_NUM_HIGHSCORE-1] = test_score;
          for(byte i= this -> _NUM_HIGHSCORE-1; i>0; i--){ //bubble sorting FTW
              if(this -> _highscore[i-1] < this -> _highscore[i]){
                  char tempName[this -> _NAME_LENGTH];
                  strcpy(tempName, this -> _name[i-1]);
                  strcpy(this -> _name[i-1], this -> _name[i]);
                  strcpy(this -> _name[i], tempName);
                  unsigned int tempScore;
                  tempScore = this -> _highscore[i-1];
                  this -> _highscore[i-1] = _highscore[i];
                  this -> _highscore[i] = tempScore;
              } else {
                  break;
              }
          }
          
          for(byte thisScore = 0; thisScore < this -> _NUM_HIGHSCORE; thisScore++){
              gb.save.set(2*thisScore, this -> _highscore[thisScore]);
              gb.save.set(2*thisScore + 1, this -> _name[thisScore]);
          }
    } // end of highscore test part
}

void Game::_showControls() {
    gb.display.drawImage(0, 0, controls);
    if (gb.buttons.pressed(BUTTON_B)) this ->_state = GameState::SHOW_OPTIONS; 
}

void Game::_showOptions() {
    gb.display.clear();
    if (this ->_initOptions) {
      _currentSelectedOption = 1;
      _initOptions = false;
    }
    uint8_t nb_options = 5;
    uint8_t line = 3;
    uint8_t col = 3;
    uint8_t vspace = 8;
    gb.display.setColor(RED);
    gb.display.drawRect(1,(_currentSelectedOption-1)* vspace + 2, 79, vspace);
    gb.display.setColor(ORANGE);
    gb.display.drawRect(1,(_currentSelectedOption-1)* vspace + 3, 79, vspace -2);
    gb.display.setColor(YELLOW);
    gb.display.fillRect(1,(_currentSelectedOption-1)* vspace + 4, 79, vspace - 4);   
    gb.display.setFontSize(1);
    if (_currentSelectedOption == 1)  gb.display.setColor(BLACK); else gb.display.setColor(LIGHTBLUE);
    gb.display.setCursor(col, line); gb.display.print("Sound:"); 
    if (this -> sound) {
      gb.display.setCursor(63, line); gb.display.print("On");
    } else {
      gb.display.setCursor(63, line); gb.display.print("Off");
    }
    line += vspace;
    if (_currentSelectedOption == 2)  gb.display.setColor(BLACK); else gb.display.setColor(LIGHTBLUE);
    gb.display.setCursor(col, line); gb.display.print("Leds:"); 
    if (this -> lightSides) {
      gb.display.setCursor(63, line); gb.display.print("On");
    } else {
      gb.display.setCursor(63, line); gb.display.print("Off");
    }
    line += vspace;
    if (_currentSelectedOption == 3)  gb.display.setColor(BLACK); else gb.display.setColor(LIGHTBLUE);
    gb.display.setCursor(col, line); gb.display.print("Show Controls"); line += vspace;
    if (_currentSelectedOption == 4)  gb.display.setColor(BLACK); else gb.display.setColor(LIGHTBLUE);
    gb.display.setCursor(col, line); gb.display.print("Show Hi-scores"); line += vspace;
    if (_currentSelectedOption == 5)  gb.display.setColor(BLACK); else gb.display.setColor(LIGHTBLUE);
    gb.display.setCursor(col, line); gb.display.print("Show Credits"); line += vspace;

    gb.display.setColor(GREEN);
    gb.display.setCursor(col, line); gb.display.print("Use arrows to move"); line += vspace;
    gb.display.setCursor(col, line); gb.display.print("<A> to validate"); line += vspace;
    gb.display.setCursor(col, line); gb.display.print("<B> to return"); line += vspace;

    if (gb.buttons.pressed(BUTTON_UP)) if (_currentSelectedOption > 1) _currentSelectedOption--;
    if (gb.buttons.pressed(BUTTON_DOWN)) if (_currentSelectedOption < nb_options) _currentSelectedOption++;
    if (gb.buttons.pressed(BUTTON_A)) {
        switch (_currentSelectedOption) {
            case 1: 
              this -> sound = !this -> sound;
            break;
            case 2: 
              this -> lightSides = !this -> lightSides;
            break;
            case 3: 
              this -> _state = GameState::SHOW_CONTROLS;
            break;
            case 4: 
              this -> _state = GameState::SHOW_SCORES;
            break;  
            case 5: 
              this -> _state = GameState::SHOW_CREDITS;
            break;       
        }
    }
    if (gb.buttons.pressed(BUTTON_B)) this -> _initTitleScreen(); 
}

